// SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity ^0.6.11;

import "./interface/IClassroomChallenge.sol";

contract ExampleChallenge is IClassroomChallenge {
    function hintsCount() external override pure returns (uint256) {
        return 2;
    }

    function getHint(uint256 index, bytes32 seed)
        public
        override
        view
        returns (bytes32)
    {
        if (index == 0) return bytes32("BATATA") | seed;
        if (index == 1) return ~bytes32("BATATA") | seed;
    }

    function viewMaterial() external override pure returns (string memory) {
        return
            "https://docs.google.com/document/d/1ml0P74-20kLsz-Lf6YB4Fte-GsmJ3KqLIZbT8XjmvwM/";
    }
}
